(126, '_transient_feed_4c74a77fb0311cda9aef46304170e010', 'a:4:{s:5:"child";a:1:{s:0:"";a:1:{s:3:"rss";a:1:{i:0;a:6:{s:4:"data";s:3:"\n	\n";s:7:"attribs";a:1:{s:0:"";a:1:{s:7:"version";s:3:"2.0";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:1:{s:0:"";a:1:{s:7:"channel";a:1:{i:0;a:6:{s:4:"data";s:132:"\n		\n		\n		\n		\n		\n		\n				\n\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n\n	";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:7:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:25:"日本語 » Recent Posts";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:32:"https://ja.forums.wordpress.org/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:15:"フォーラム";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"language";a:1:{i:0;a:5:{s:4:"data";s:2:"ja";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 09:26:35 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:9:"generator";a:1:{i:0;a:5:{s:4:"data";s:25:"http://bbpress.org/?v=1.1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"item";a:35:{i:0;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:69:"ayaxkawa :  &quot;カスタム投稿の投稿者アーカイブ&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149145#post-206870";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 08:17:48 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206870@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:872:"<p>Wordpress4.1.1で、カスタム投稿を利用してサイトを構築しようと思っています。</p>\n<p>カスタム投稿で、投稿者アーカイブを使いたいのですが、<br />\nカスタム投稿名/ユーザー名（例えば hoge/admin）のURLで、<br />\n投稿者別アーカイブを出すようにするには<br />\nどうすれば良いのでしょうか。</p>\n<pre><code>add_filter( &#039;rewrite_rules_array&#039;, &#039;my_rewrite_rules_array&#039; );\nfunction my_rewrite_rules_array( $rules ) {\n	$new_rules = array(\n&#039;hoge/([a-z]+)/?$&#039; =&#62; &#039;index.php?post_type=hoge&#38;author=$matches[1]&#039;,\n	return $new_rules + $rules;\n}</code></pre>\n<p>で、カスタム投稿名/ユーザーID　は実現できているのですが、<br />\nこの後で詰まっております。</p>\n<p>よろしくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"ayaxkawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:1;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:113:"minoword :  &quot;MTS Simple Bookin C Ver.1.2.1で予約確定をした時点でメールを配信したい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149144#post-206869";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 07:57:18 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206869@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:2891:"<p>お世話になります。<br />\nminowordと申します</p>\n<p>【やりたいこと概要】<br />\nMTS Simple Bookinを使用して予約機能を構築しています。<br />\n既存機能にて予約者がカレンダーから予約をして[予約する]ボタンを<br />\n押下すると予約者と管理者に自動でメールが配信されます。<br />\nただし、この時点では予約が確定したわけではないので、<br />\n管理者から予約確定のメールを送信しなければなりません。<br />\nそこで、予約確定のメールも確認したタイミングでメールを自動送信したいと考えています。<br />\n具体的には管理画面の予約の編集画面にて予約確認済みにチェック<br />\nを入れて[予約データ保存]ボタンを押下したタイミングでメールを<br />\n送信したいと考えております。</p>\n<p>【質問】<br />\n管理画面の「予約システム」-&#62;「予約リスト」-&#62;「任意の予約の編集画面」で[予約データ保存]ボタンを押下し、ページ更新したタイミングで自動でメールを送るかどうかの判定をしたいのですが、そのページの更新を受けるフィルター/アクションが分からず教えて頂けると助かります。</p>\n<p>試しにアクションフックの”edit_post”に既存のinternal_dispatcher関数をコールを登録して関数の先頭でコメントを出すようにして実行してみたのですが、コールバックされませんでした。</p>\n<p>入れた場所のソースは以下です。<br />\nmts-simple-booking.php<br />\nのinit_mtssb関数<br />\n<pre><code>/**\n	 * プラグイン初期化処理\n	 *\n	 */\n	public function init_mtssb()\n	{\n\n		// その他設定の読み込み\n		$miscellaneous = get_option(self::DOMAIN . &#039;_miscellaneous&#039;);\n\n		// MTS Customerプラグインの組み込み確認\n		if (!function_exists(&#039;is_plugin_active&#039;)) {\n			require_once(ABSPATH . &#039;wp-admin/includes/plugin.php&#039;);\n		}\n		if (is_plugin_active(&#039;mts-customer/mts-customer.php&#039;)) {\n			$this-&#62;mtscu_activation = true;\n		}\n\n		if (is_admin()) {\n\n			// ユーザーロールがcustomerなら管理画面を表示させない\n                        *****中略****\n\n			//ここに入れました。\n　　　　　　　　　　　　//試しに既存のinternal_dispatcher関数を呼び出して関数の先頭でコメントを出力させています。\n			add_action(&#039;edit_post&#039;, array($this, &#039;internal_dispatcher&#039;));\n\n			// 予約品目処理オブジェクトのロード\n			require_once(&#039;mtssb-article-admin.php&#039;);\n			$oArticle = new MTSSB_Article_Admin();\n\n		} else {\n                 *****以降省略****</code></pre>\n<p>以上、よろしくお願いいたします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"minoword";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:2;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:105:"miwarock777 :  &quot;英語サイトで管理画面を日本語表示する方法はありますか&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149047#post-206868";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 07:10:36 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206868@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:461:"<p>맹조さま、コメントありがとうございました。</p>\n<p>Wordpress Language、早速インストールしました。<br />\n意図通りの表示になりました。<br />\nなんて便利なプラグインなのでしょうか、、、<br />\n教えていただきありがとうございました！！</p>\n<p>つい最近にも同じコメントがあったのですね…<br />\n調べずに投稿をしてしまいすみませんm(_ _)m\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:11:"miwarock777";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:3;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:91:"맹조 :  &quot;「最近の投稿」の自動的な表示を消す方法について&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149135#post-206867";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 06:42:44 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206867@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1100:"<p>広告サイトは固定ページに作成して、それをフロントページに使用するのがこれまでの使い方でしたね。</p>\n<blockquote><p>ただ、このテーマでは、「カスタマイズ」での「フロントページの表示」で、「最新の投稿」を選ぶと、</blockquote>\n投稿のループの上にPR用のセクションが挿入されるので、簡単に広告サイトを作成できます。</p>\n<p>『最新の投稿（LATEST NEWS）』の表示自体を消すには、スタイルシートでセクションごと隠してしまうかフロントページのインデックスから取り除いてしまうということになります。<br />\nそれでは本末転倒で、固定ページに『最新の投稿（LATEST NEWS）』以外を持ってくるということになります。テーマ製作者の意図と配慮、努力に無礼にあたると、わたしは考えます。</p>\n<p>ちなみに、「外観」→「theme options」で『Short Codes』タブの選択で用意されているコードを確認できますよ。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:4;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:110:"muro :  &quot;MW WP Form functions.phpでバリデーション設定されているとエラーになる&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149141#post-206866";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 06:09:21 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206866@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:171:"<p>修正していただき、ありがとうございます。<br />\nページの読み込みもバリデーションも、無事行えるようになりました。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"muro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:5;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:122:"Takashi Kitajima :  &quot;MW WP Form functions.phpでバリデーション設定されているとエラーになる&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149141#post-206865";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 05:42:26 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206865@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:360:"<p>2.3.5 から 2.4 にバージョンアップする過程でメソッドの整理を行ったのですが、そのときに set_rule のアクセス権を誤って変えてしまっていたようです。ご報告ありがとうございます。さきほど修正をいれたバージョン 2.4.3 をアップしましたのでご確認ください。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:16:"Takashi Kitajima";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:6;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:108:"mitsu666 :  &quot;カスタムフィールドの複数の値に合致したリストを表示したい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149024#post-206864";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 05:38:22 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206864@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1156:"<p>hogetanさま</p>\n<p>お世話になっております。<br />\n返信が大変遅くなりもうしわけありません！</p>\n<p>''&#60;?php if(have_posts()): ?&#62;<br />\n	&#60;tr&#62;&#60;th colspan="3" class="area"&#62;首都圏&#60;/th&#62;&#60;/tr&#62;</p>\n<p>	&#60;?php<br />\n	$args = Array(<br />\n		''post_type'' =&#62; ''shoplist'',<br />\n		''posts_per_page'' =&#62; -1,<br />\n		''meta_key'' =&#62; ''shoplist_area'',<br />\n		''meta_value'' =&#62; ''首都圏'',<br />\n		''meta_query'' =&#62; array(array(<br />\n						''key'' =&#62; ''shoplist_01'',<br />\n						''value'' =&#62; ''SHOP''<br />\n		))<br />\n		);<br />\n	$the_query = new WP_Query($args);<br />\n	if($the_query -&#62; have_posts()):<br />\n		while($the_query -&#62; have_posts()): $the_query -&#62; the_post();<br />\n	 ?&#62;</p>\n<p>	 表示内容</p>\n<p>	 &#60;?php endwhile; wp_reset_postdata(); ?&#62;<br />\n	&#60;?php endif; wp_reset_postdata(); ?&#62;<br />\n&#60;?php endif; ?&#62;''</p>\n<p>あれからいろいろ試してみて、<br />\n以上にて自己解決いたしました。<br />\nご教授ありがとうございます！<br />\n今後とも何卒よろしくお願いいたします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"mitsu666";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:7;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:94:"맹조 :  &quot;phpバージョンアップ後、データベース接続確立エラー&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149143#post-206863";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 05:25:29 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206863@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:125:"<p>同じサーバーに php5.6.7 で、もう一つ WordPress サイトを作って比較したらどうでしょう。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:8;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:102:"wakuwaku.tombo :  &quot;phpバージョンアップ後、データベース接続確立エラー&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149143#post-206862";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 05:17:14 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206862@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1115:"<p>php5.3.3からphp5.6.7にバージョンアップを実施後<br />\nサイトURLで「データベース接続確立エラー」表示、<br />\n管理画面URLで下記エラー表示がでます。<br />\nデータベース接続確立エラー<br />\nこれは wp-config.php ファイルのユーザー名とパスワード情報が間違っているか<br />\nxxx.xxx.xxx.xxxのデータベースサーバーに接続できないかのどちらかを意味します。ホストのデータベースサーバーがダウンしているかもしれません。<br />\nユーザー名とパスワードか正しいか確認してください。<br />\n正しいホスト名を入力しているか確認してください。<br />\nデータベースサーバーが動作中か確認してください。</p>\n<p>ユーザー名・パスワード・ホスト名は変更しておらず、<br />\nデータベースサーバーも稼動しております。<br />\n何か設定を変更する必要があるのでしょうか？<br />\nMySQLのバージョン　：　5.0.77</p>\n<p>どなたかご教示願います。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:14:"wakuwaku.tombo";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:9;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:80:"t_kawa :  &quot;Background Update TesterのWARNINGメッセージの対応&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149142#post-206861";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 05:05:29 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206861@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:738:"<p>初歩的な質問ですが、宜しくお願いします。<br />\nBackground Update Testerを実行すると、以下のメッセージが表示されます。</p>\n<p>PASS: Your WordPress install can communicate with WordPress.org securely.<br />\nWARNING: A previous automatic background update could not occur. You would have received an email because of this. We’ll try again with the next release. The error code was mkdir_failed_ziparchive.<br />\nPASS: No version control systems were detected.<br />\nPASS: Your installation of WordPress doesn’t require FTP credentials to perform updates.<br />\nPASS: All of your WordPress files are writable.</p>\n<p>WARNINGメッセージの対応について、ご指導ください。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"t_kawa";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:10;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:100:"맹조 :  &quot;英語サイトで管理画面を日本語表示する方法はありますか&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149047#post-206860";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 05:05:23 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206860@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:583:"<blockquote><p>プラグインの<br />\n「Wordpress Language」</p>\n<p>ですべて解決できると思います。<br />\nこれをインストールして頂いて、ダッシュボード→設定→言語</p>\n<p>で、管理画面とページの言語を一つ一つ管理できます。</p></blockquote>\n<p>４日前のトピックに、紹介されているプラグインで対応できないですか。<br />\n<a href="https://ja.forums.wordpress.org/topic/149098?replies=4" rel="nofollow">bogoで管理画面を日本語に、ブログは英語で表示したい。</a>\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:11;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:105:"miwarock777 :  &quot;英語サイトで管理画面を日本語表示する方法はありますか&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149047#post-206859";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 04:58:17 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206859@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:905:"<p>少し時間が経ちましたが、、、<br />\n結局、私の意図通りの挙動にはなっていなかったため再度【未解決】に戻しますm(_ _)m</p>\n<p>＜やりたいこと＞<br />\n管理画面を日本語（←ダッシュボードのメニューの項目が英語だと分からないため）<br />\nサイトの言語は英語（サイト自体は英語としたい）<br />\n投稿の文章やタイトルなどは英文で投稿する</p>\n<p>とした場合、記事のロケールは「日本語」となり、<br />\n各ページのURLには、◯◯◯◯◯/?p=ページID&#38;lang=ja<br />\nと、パーマリンクのあとにlang=jaがついてしまいます。</p>\n<p>サイト自体の言語は英語でよく、管理画面のメニューなどが日本語であればよいのですが、<br />\nそのようなことは出来ないのでしょうか、、、？\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:11:"miwarock777";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:12;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:110:"muro :  &quot;MW WP Form functions.phpでバリデーション設定されているとエラーになる&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149141#post-206858";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 04:22:24 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206858@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:5356:"<p>MW WP Formのバージョンが2.3.5のときは正常だったのですが、2.4.1および2.4.2にアップデートしてから、mwformを使用している固定ページを開くと、下記のようなエラーメッセージが書かれたページが表示されるようになってしまいました。<br />\n<pre><code>Fatal error: Call to protected method MW_WP_Form_Validation::set_rule() from context &#039;&#039; in /vagrant/example.com/wp-sys/wp-content/themes/example.com/functions.php on line 412</code></pre>\n<p>functions.phpには下記のバリデーション設定用の関数を記述しており、これがエラーを起こしているようです。<br />\n上記のエラーメッセージでline 412となっているのは、初めのset_ruleです。<br />\nただし初めのset_ruleだけがおかしいというわけではなく、set_ruleが何か一つ存在するだけでエラーになります。<br />\n<pre><code>add_filter( &#039;mwform_validation_contact&#039;, &#039;mwform_validation_contact&#039; );\nfunction mwform_validation_contact( $v ) {\n	$v-&#62;set_rule( &#039;会社名&#039;, &#039;noEmpty&#039; );\n	$v-&#62;set_rule( &#039;お名前&#039;, &#039;noEmpty&#039; );\n	$v-&#62;set_rule( &#039;フリガナ&#039;, &#039;noEmpty&#039; );\n	$v-&#62;set_rule( &#039;郵便番号&#039;, &#039;zip&#039; );\n	$v-&#62;set_rule( &#039;電話番号&#039;, &#039;tel&#039; );\n	$v-&#62;set_rule( &#039;FAX番号&#039;, &#039;tel&#039; );\n	$v-&#62;set_rule( &#039;メールアドレス&#039;, &#039;noEmpty&#039; );\n	$v-&#62;set_rule( &#039;メールアドレス&#039;, &#039;mail&#039; );\n	$v-&#62;set_rule( &#039;メールアドレス(確認用)&#039;, &#039;noEmpty&#039; );\n	$v-&#62;set_rule( &#039;メールアドレス(確認用)&#039;, &#039;mail&#039; );\n	$v-&#62;set_rule( &#039;メールアドレス(確認用)&#039;, &#039;eq&#039;, array(&#34;target&#34;=&#62;&#34;メールアドレス&#34;));\n	$v-&#62;set_rule( &#039;プライバシーポリシー&#039;, &#039;required&#039;, array(&#34;message&#34;=&#62;&#34;プライバシーポリシーに同意する必要があります。&#34;) );\n	$v-&#62;set_rule( &#039;お問い合わせ内容&#039;, &#039;noEmpty&#039; );\n	return $v;\n}</code></pre>\n<p>また、mwformを使用している固定ページの内容は下記の通りです。<br />\n<pre><code>&#60;section id=&#34;page_contact&#34;&#62;\n	&#60;h3 class=&#34;page_title&#34;&#62;お問い合わせフォーム&#60;/h3&#62;\n	[mwform key=&#34;contact&#34; confirm=&#34;http://192.168.33.12/example.com/contact/confirm&#34;]\n		&#60;table&#62;\n			&#60;tr&#62;\n				&#60;th class=&#34;required&#34;&#62;会社名&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div&#62;[mwform_text name=&#34;会社名&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th class=&#34;required&#34;&#62;お名前&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div&#62;[mwform_text name=&#34;お名前&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th class=&#34;required&#34;&#62;フリガナ&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div&#62;[mwform_text name=&#34;フリガナ&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th&#62;住所&#60;/th&#62;\n				&#60;td class=&#34;address&#34;&#62;\n					&#60;div class=&#34;zip&#34;&#62;[mwform_zip name=&#34;郵便番号&#34;]&#60;/div&#62;\n					&#60;div&#62;[mwform_text name=&#34;住所1&#34;]&#60;/div&#62;\n					&#60;div&#62;[mwform_text name=&#34;住所2&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th&#62;電話番号&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div class=&#34;tel&#34;&#62;[mwform_tel name=&#34;電話番号&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th&#62;FAX番号&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div class=&#34;tel&#34;&#62;[mwform_tel name=&#34;FAX番号&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th class=&#34;required&#34;&#62;メールアドレス&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div&#62;[mwform_text name=&#34;メールアドレス&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th class=&#34;required&#34;&#62;メールアドレス&#60;br /&#62;(確認用)&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div&#62;[mwform_text name=&#34;メールアドレス(確認用)&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n			&#60;tr&#62;\n				&#60;th class=&#34;required&#34;&#62;お問い合わせ内容&#60;/th&#62;\n				&#60;td&#62;\n					&#60;div&#62;[mwform_textarea name=&#34;お問い合わせ内容&#34;]&#60;/div&#62;\n				&#60;/td&#62;\n			&#60;/tr&#62;\n		&#60;/table&#62;\n		&#60;div class=&#34;check&#34;&#62;[mwform_checkbox name=&#34;プライバシーポリシー&#34; children=&#34;同意する&#34;]&#60;/div&#62;\n		&#60;div class=&#34;button_wrap&#34;&#62;\n			&#60;div&#62;[mwform_confirmButton value=&#34;確認画面&#34;]&#60;/div&#62;\n			&#60;div&#62;&#60;input type=&#34;button&#34; class=&#34;reset&#34; value=&#34;リセット&#34; /&#62;&#60;/div&#62;\n		&#60;/div&#62;\n	[/mwform]\n&#60;/section&#62;</code></pre>\n<p>バリデーションの設定方法が変更になったのでしょうか？<br />\nそれとも他に修正しなければいけない点があるのでしょうか？\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:4:"muro";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:13;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:97:"Hidefumi Oga :  &quot;「最近の投稿」の自動的な表示を消す方法について&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149135#post-206857";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 02:41:28 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206857@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:475:"<p>追記です。</p>\n<p>テーマカスタマイザーというものがあることが、調べているとわかりました。</p>\n<p><a href="http://wpdocs.sourceforge.jp/Theme_Customization_API" rel="nofollow">http://wpdocs.sourceforge.jp/Theme_Customization_API</a></p>\n<p>どうやら、このテーマの開発者は、これを使って製作されたのでしょう。後は、内容を、どうやって編集すればいいか、になりましょうか。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Hidefumi Oga";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:14;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:95:"izu2014 :  &quot;『コメントは受け付けていません』を非表示にしたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149132#post-206856";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 02:21:06 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206856@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:171:"<p>追記：<br />\nPCでは非表示になりましたが、スマホではまだ表示されています。<br />\nこれは仕方ないものなのでしょうか？\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"izu2014";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:15;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:97:"Hidefumi Oga :  &quot;「最近の投稿」の自動的な表示を消す方法について&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149135#post-206855";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 01:52:10 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206855@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:996:"<p>맹조さん　いつもありがとうございます。</p>\n<p>　表示数を指定するには、テーマの編集で、どのコーナーにあるか、探したのですが、見つけられませんでした。</p>\n<p>　ただ、このテーマでは、「カスタマイズ」での「フロントページの表示」で、「最新の投稿」を選ぶと、投稿が無限に出てくるだけでなく、その上に、このテーマ独特の、組織の紹介ができるコーナー（セクション）が現れることがわかりました。<br />\n　ちなみに、この投稿が表示されるコーナーのページは、「外観」→「theme options」で編集するようになっています。</p>\n<p>　表示数がゼロにできたとしても、　「LATEST  NEWS」の表示自体を消すには、どうしたらいいいのでしょか。</p>\n<p>　当方、初心者で、申し訳ないですが、どうか、よろしくおねがいします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Hidefumi Oga";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:16;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:89:"yuzz_ponzz :  &quot;孫カテゴリ名を除く子カテゴリ名を取得したい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149140#post-206854";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 01:50:05 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206854@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1878:"<p>はじめまして、いつもお世話になっております。<br />\n初心者なのですがご教授いただけますと幸いです。</p>\n<p>TOPページの新着情報を作成していて、記事のカテゴリごとのCSSを切り替えるためにカテゴリスラッグを取得しています。</p>\n<p>以下は現在のコードです。</p>\n<pre><code>&#60;?php\n$cats = get_the_category();\n$current_cat = &#039;&#039;;\nforeach ( $cats as $cat ) {\n  if ( ! $current_cat &#124;&#124; cat_is_ancestor_of( $current_cat, $cat ) ) {\n    $current_cat = $cat;\n  }\n}\n?&#62;\n\n&#60;dd class=&#34;&#60;?php echo &#34;$current_cat-&#62;category_nicename&#34;; ?&#62;&#34;&#62;\n&#60;span class=&#34;topics-catgory&#34;&#62;\n&#60;?php echo $current_cat-&#62;cat_name; ?&#62;\n&#60;/span&#62;\n&#60;/dd&#62;\n&#60;a class=&#34;topics-name&#34; href=&#34;&#60;?php the_permalink(); ?&#62;&#34;&#62;&#60;?php the_title(); ?&#62;&#60;/a&#62;\n&#60;?php endwhile;?&#62;</code></pre>\n<p>日付、カテゴリ名、記事名　としたいのですが<br />\nカテゴリが、</p>\n<p>大カテゴリ１　ー　子カテゴリ１<br />\n　　　　　　　ー　子カテゴリ２<br />\n　　　　　　　ー　子カテゴリ３　ー孫カテゴリ１<br />\n　　　　　　　　　　　　　　　　ー孫カテゴリ２</p>\n<p>大カテゴリ２　ー　子カテゴリ４<br />\n　　　　　　　ー　子カテゴリ５</p>\n<p>となっており、子カテゴリのスラッグ、カテゴリ名のみを取得したいのですが<br />\n現在のコードですと直近のカテゴリ名を取得しているため、子カテゴリ３と孫カテゴリ１に所属している記事のときには、孫カテゴリ名で表示されてしまいます。</p>\n<p>ご回答いただけます大変助かります。<br />\nよろしくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"yuzz_ponzz";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:17;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:84:"boss1951 :  &quot;WordPress Popular Postsのカウンター表示が0になる&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149139#post-206853";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 01:20:02 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206853@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:692:"<p>WordPress Popular Postsを利用して記事のPV数を表示させています。</p>\n<p>カスタム投稿タイプAの記事ではきちんと表示されていて、新たにカスタム投稿タイプBを作った際に同じように記述したのですが、なぜか「0views」となります。</p>\n<pre><code>&#60;p class=&#34;counts&#34;&#62;\n&#60;?php echo wpp_get_views($post-&#62;ID,&#039;all&#039;); ?&#62; views\n&#60;/p&#62;</code></pre>\n<p>上記をループの中に記述しています。<br />\n設定画面のランキングではカウントされているのですが。</p>\n<p>何が原因か分からず困っています。よろしくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"boss1951";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:18;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:95:"suzienuzie :  &quot;特定日付または曜日のアーカイブページを作りたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149062#post-206852";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 01:06:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206852@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:764:"<p>nobitaさま、レスありがとうございます。<br />\ndate.phpを拝見しましたが、やはり高度すぎて<br />\n何がどうなっているのか私にはさっぱりでした…<br />\nせっかくご提示いただきましたのに申し訳ございません。</p>\n<p>あきらめきれずいろいろとググっておりましたら<br />\nカスタム投稿タイプを使用するやり方を見つけまして、<br />\n初心者でもまだ何とかできそうな感じでしたので<br />\nひとまずそちらを挑戦してみようかと思います。<br />\n<a href="http://www.webopixel.net/wordpress/343.html" rel="nofollow">http://www.webopixel.net/wordpress/343.html</a></p>\n<p>今回はありがとうございました。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:10:"suzienuzie";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:19;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:100:"saki77 :  &quot;記事のアイキャッチ画像にオリジナル画像を表示させたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149138#post-206851";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 00:55:03 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206851@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:861:"<p>お世話になります。<br />\nWordPress 4.1.1 (Twenty Fifteenテーマ)のアイキャッチ画像について教えて下さい。</p>\n<p>WPによって画質が圧縮されてリサイズされた画像ではなく、自分でメディアライブラリに<br />\n追加した画像そのままを、記事のアイキャッチ画像として表示させる方法を探しています。</p>\n<p>私は自分で画質を調整してトリミングした画像をメディアライブラリにUPしていまして、<br />\nこれをアイキャッチに指定すると、WPによって圧縮されて画質が悪くなりファイルサイズも<br />\n増えてしまうので、自分で用意した画像をそのまま表示させたいのです。</p>\n<p>方法がありましたら教えて下さい。<br />\nよろしくお願い致します。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"saki77";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:20;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:95:"izu2014 :  &quot;『コメントは受け付けていません』を非表示にしたい&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149132#post-206850";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Wed, 08 Apr 2015 00:47:19 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206850@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:99:"<p>nobitaさん、できました！<br />\n助かりましたありがとうございます！\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"izu2014";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:21;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:54:"purebui :  &quot;the_permalinkが一致しない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149137#post-206849";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 21:35:16 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206849@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:1617:"<p>お世話になっております。<br />\n自作テーマにて、行き詰まってしまいました。<br />\n内容としまして、query_postsをカテゴリー指定した場合のthe_permalinkが一致しません。</p>\n<p>この様に書いています。</p>\n<pre><code>&#60;?php query_posts(&#039;showposts=6&#38;offset=1&#38;cat=23&#039;); ?&#62;\n &#60;?php if (have_posts()) :\n				while (have_posts()) :\n					the_post(); ?&#62;\n &#60;div class=&#34;sub_container&#34;&#62;\n\n&#60;div class=&#34;sub_timestamp&#34;&#62;&#60;?php echo get_the_date(); ?&#62;&#60;?php the_time(); ?&#62;&#60;/div&#62;\n&#60;a href=&#34;&#60;?php the_permalink(); ?&#62;&#34;&#62;\n&#60;div class=&#34;box_img&#34;&#62;\n&#60;?php the_post_thumbnail( array(161, 164) ); ?&#62;\n&#60;div class=&#34;sub_Title&#34;&#62;&#60;?php echo get_the_title(); ?&#62;&#60;/div&#62;&#60;/a&#62;\n\n&#60;!--box_img_END--&#62;&#60;/div&#62;\n\n&#60;!--sub_container_END--&#62;&#60;/div&#62;\n&#60;?php endwhile;\n		else : ?&#62;\n	&#60;p&#62;お探しの記事は見つかりませんでした。&#60;/p&#62;\n\n&#60;?php endif; ?&#62;</code></pre>\n<p>投稿日時やアイキャッチ画像、タイトル等はちゃんと一致しているのですが実際に個別ページに移動すると、最新記事の内容に移動してしまいます。<br />\nこの場合、どのようにすれば投稿記事とpermalinkが一致しますでしょうか？</p>\n<p>また、カテゴリー指定の場合の正しい書き方等有りましたら、ご教授ください。<br />\n伝わりづらいですが、よろしくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"purebui";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:22;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:99:"hop01 :  &quot;WordPress内のページの読み取りが遅いもしくは表示できない&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149136#post-206848";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 20:35:32 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206848@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:3058:"<p>こんにちは。</p>\n<p>ここ数日、Wordpress内のページの読み取りが遅いもしくは表示できない状態にあります。<br />\nサーバーに問い合わせたところ、データベースへのリクエストが高く、読み取り時間が<br />\n異様にかかると指摘されました。原因・処置・対応として教えられたのは</p>\n<p>・Wordpressのプログラムに問題<br />\n　　　レイアウトのカスタマイズ以外には何も変更していません。<br />\n　　　　（プログラムの改変は出来ないので）もしくは新しいバージョンへの更新のみ</p>\n<p>・キャッシュの処理<br />\n・サイトのオプティマイズ<br />\n　　　二つのプラグイン（wp-optimize、WP super cache）を試しましたが<br />\n　　　特に変化なくページの表示、ログイン共に出来ない状態のまま</p>\n<p>Wordpressには、welcart, wp-jquery-lightbox, wp-multibyte-patchの<br />\nプラグインを使用しています。どれも最新のものです。</p>\n<p>引き続き、サーバーに問い合わせたところ<br />\n・不要、未使用のテーマ、プラグインを削除<br />\n・もしくは、プラグインを一つのみ稼動し、どれが稼動に影響しているか探る。</p>\n<p>ですが、一日の内に数回、短い時間だけ運よく表示されログイン出来ますが<br />\nいつかは分からず、その時にPCに向かっているとは限らず、基本的に<br />\n「ログインして削除、稼動停止してみろ」と言われてもどうにも出来ない状態です。</p>\n<p>そこで、サーバーのアカウントから直接、不要なものを削除すればと考えています。<br />\nここでお聞きしたいのですが、</p>\n<p>・ xxxxx.com/shop/wp-content/themes/twentyfifteen<br />\nとある「twentyfifteen」のフォルダを削除した場合に<br />\nほか全体のWordpressに影響はありますか？</p>\n<p>・データベース内にある「wp_posts」を見ると、リビジョンらしき履歴（投稿？）が<br />\nあるのが分かります。それぞれのタイトル（投稿）毎に複数あるのですが、<br />\n最新以外のものを削除しても、最終の訂正版の投稿は残るという認識で大丈夫でしょうか？</p>\n<p>・また、リビジョンを設定できるプラグインがあるようですが、プラグインを<br />\n使用せずプログラムの一部を変更してすることは可能でしょうか？<br />\nありましたら、ご指導いただけると幸いです。</p>\n<p>よろしくお願いします。</p>\n<p>*＊＊＊＊＊＊＊＊＊＊＊＊＊＊＊＊＊</p>\n<p>申し訳ありません。「使い方全般」に投稿したつもりだったのですが<br />\nスタッフ専用のカテゴリーに投稿してしまったようです。<br />\n不都合であれば、削除してくださって結構です。<br />\n改めてお詫び致します。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"hop01";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:23;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:91:"맹조 :  &quot;「最近の投稿」の自動的な表示を消す方法について&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149135#post-206847";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 18:29:41 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206847@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:86:"<p>ショートコードで表示数を指定するだけです。簡単です。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:24;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:97:"Hidefumi Oga :  &quot;「最近の投稿」の自動的な表示を消す方法について&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149135#post-206846";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 17:20:57 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206846@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:844:"<p>SKT　Whiteというフリーのテーマをつかっていますが、ヘッダーの一つ上に<br />\n「Latest News」というコーナーがあり、最近の投稿が、勝手に表示されてしまいます。</p>\n<p><a href="http://hlc-rc.jp/tokyo-medical-mindfulness-center/" rel="nofollow">http://hlc-rc.jp/tokyo-medical-mindfulness-center/</a></p>\n<p>レスポンシブでのスマホ対応の表示をスマホで見たら、HPの本体よりも投稿記事ばかりが表示されるため、CSSや、singule-post.phpの該当部分をコメントアウトしてみたりして、格闘したのですが、どうしても消せません。</p>\n<p>どなか、消し方をご指南いただけないでしょうか？</p>\n<p>当方、初心者ですので、簡単な方法を教えていただければ、幸いです。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:12:"Hidefumi Oga";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:25;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:104:"juckpot :  &quot;絞込み検索後のAdvanced Custom Fieldsのrepeater fieldの値でのソート&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149134#post-206845";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 14:07:30 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206845@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:3150:"<p>初めて投稿させていただきます。<br />\n他に似たような投稿が見当たらなかったので質問させていただきます。<br />\nやりたいこととしては複数の条件による検索をした後に、更にその表示された検索結果セレクトボックスで「値段の高い順」「値段の安い順」を選択できるようにして、ソートしたいと考えています。<br />\n絞り込み検索にかんしては<a href="http://kotori-blog.com/wordpress/refinement_search/" rel="nofollow">こちらのブログ</a>を参考に実装させました。<br />\nしかし、その後のソートが知識不足のためうまくいきません。<br />\n<pre><code>&#60;?php\n$s = $_GET[&#039;s&#039;];\n$day = $_GET[&#039;day&#039;];\n$purpose = $_GET[&#039;purpose&#039;];\n$word = $_GET[&#039;word&#039;];\n$year = $_GET[&#039;year&#039;];\n$month = $_GET[&#039;month&#039;];\n$country = $_GET[&#039;country&#039;];\n?&#62;\n\n&#60;?php\nif($purpose){\n    $taxquerysp[] = array(\n            &#039;taxonomy&#039;=&#62;&#039;purpose&#039;,\n            &#039;terms&#039;=&#62; $purpose,\n            &#039;include_children&#039;=&#62;false,\n            &#039;field&#039;=&#62;&#039;slug&#039;,\n            &#039;operator&#039;=&#62;&#039;IN&#039;\n            );\n}\n\nif($word){\n    $taxquerysp[] = array(\n            &#039;taxonomy&#039;=&#62;&#039;word&#039;,\n            &#039;terms&#039;=&#62; $word,\n            &#039;include_children&#039;=&#62;false,\n            &#039;field&#039;=&#62;&#039;slug&#039;,\n            &#039;operator&#039;=&#62;&#039;IN&#039;\n            );\n}\n$taxquerysp[&#039;relation&#039;] = &#039;AND&#039;;\n\nif($day){\n    $metaquerysp[] = array(\n            &#039;key&#039;=&#62;&#039;day&#039;,\n            &#039;value&#039;=&#62; $day,\n            &#039;compare&#039;=&#62;&#039;LIKE&#039;,\n            );\n}\nif($year){\n    $metaquerysp[] = array(\n            &#039;value&#039;=&#62; $year,\n            &#039;compare&#039;=&#62;&#039;=&#039;,\n            );\n}\nif($month){\n    $metaquerysp[] = array(\n            &#039;value&#039;=&#62; $month,\n            &#039;compare&#039;=&#62;&#039;=&#039;,\n            );\n}\nif($country){\n    $metaquerysp[] = array(\n            &#039;key&#039;=&#62;&#039;country&#039;,\n            &#039;value&#039;=&#62; $country,\n            &#039;compare&#039;=&#62;&#039;=&#039;,\n            );\n}\n$metaquerysp[&#039;relation&#039;] = &#039;AND&#039;;\n?&#62;\n&#60;?php\n$args = array(\n    &#039;tax_query&#039; =&#62; $taxquerysp,\n    &#039;meta_query&#039; =&#62; $metaquerysp,\n    &#039;s&#039; =&#62; $s,\n    &#039;posts_per_page&#039; =&#62; 10,\n    &#039;paged&#039; =&#62; $paged,\n    );\nquery_posts($args);\n?&#62;</code></pre>\n<p>こちらで絞込みまでは出来るようになったのですが、ソートをするのにquery_postsにmeta_keyを指定したりしたのですが、ACFのサブフィールドだとkeyが取得できない？のかカスタムフィールドを指定してもソートされませんでした。<br />\nどなたかお力を貸していただけないでしょうか？<br />\nよろしくお願いします。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"juckpot";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:26;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:61:"gcp :  &quot;Contact Form 7 特定の文字が抜ける&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148990#post-206844";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 14:05:26 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206844@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:110:"<p>Takayuki Miyoshi　さま</p>\n<p>かしこまりました、ご返信ありがとうございます。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:3:"gcp";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:27;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"맹조 :  &quot;サイトタイトルが消えた&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148632#post-206843";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 13:44:03 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206843@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:900:"<p>新しくドメインを作ってワードプレスサイトを新規で作成して試してみました。</p>\n<blockquote><p>左の枠「濃いグリーン」</blockquote>\nと曖昧な表現をされている、アーカイヴ・ページのコーナー（background-color:#8AB7AD）部分もスタイルシートで非表示に出来ました。<br />\nこの部分はウィジェットではなくテーマで定義されていました。</p>\n<blockquote><p>なぜならカテゴリーの順番が五十音順でバラバラだからです。</blockquote>\nカテゴリーやタグの並び順は、テーマが外国圏で作ってあるのでひらがな、漢字を使うところまでは考慮されていませんから、わたしは英表記にしています。<br />\nスラッグで昇順、降順にするのも可能だし、任意に並べるプラグインもあります。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:28;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:121:"gblsm :  &quot;複数のカスタム投稿タイプで1つのカスタム分類を用いた際のテンプレート&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148581#post-206842";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 13:19:00 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206842@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:251:"<p>Toro_Unitさん、コメントありがとうございます。テンプレートの読み込み順も影響受けてしまうんですね。うまく働いていないみたい…なんて言ってしまい申し訳ありませんでした。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:5:"gblsm";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:29;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:57:"MINTBABY :  &quot;サイトタイトルが消えた&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148632#post-206841";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 12:39:47 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206841@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:475:"<p>ありがとうございます。<br />\n普段、外観＞ウィジェットからカスタマイズしているのですが、<br />\nこのページの設定がありません。<br />\nサイトの下の段、もしくは記事を開いたサイドバーの設定の２つしかありません。</p>\n<blockquote><p>これはスラッグで並べ替えできます。</p></blockquote>\n<p>スラッグに１などの数字を入れるということでしょうか。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"MINTBABY";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:30;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:121:"kedama :  &quot;レスポンシブデザインで指定した値より小さい横幅で切り替わってしまう&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/149131#post-206840";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 12:37:33 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206840@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:749:"<p>x743さん、ありがとうございます。<br />\nご指摘のありましたスクロールバー分のサイズですが、スクロールバーを含めた状態で確認しています。<br />\nｊQueryは使用しておらず、CSSに記述したメディアクエリだけで切り替えをしようとしています。</p>\n<p>現在は481px、736px、960pxで切り替えようとしていますが、433Px、663px、864pxでそれぞれ変わってしまう状態です。<br />\nローカルで同じようにChromeの要素検証で確認した時は設定通りに動いていましたので、もう一度ローカルで動作確認をしてみようと思います。<br />\n貴重な回答ありがとうございました。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"kedama";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:31;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:63:"doido44 :  &quot;RSS importでgooブログの読み込み&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:55:"https://ja.forums.wordpress.org/topic/23186#post-206839";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 12:36:58 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206839@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:405:"<p>同じことで悩んでて原因を発見したので書いときます。</p>\n<p>gooブログではrssでは日付データがdc:dateで書かれているようでpubDateからとりだそうとしても何も取り出せず、日付が表示できません。</p>\n<p>以下を参照ください。<br />\n<a href="http://on-ze.com/archives/1778" rel="nofollow">http://on-ze.com/archives/1778</a>\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:7:"doido44";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:32;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"맹조 :  &quot;サイトタイトルが消えた&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148632#post-206838";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 11:42:30 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206838@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:401:"<blockquote><p>アーカイヴに表示される見出しの文字列、◯◯のアーカイヴ、◯◯のカテゴリーについてはわかりません。</p></blockquote>\n<p>その点については、<br />\n<pre><code>&#60;style type=&#34;text/css&#34;&#62;\n&#60;!--\ndiv.columns.sixteen, .quick-note {\ndisplay:none;\n}\n--&#62;\n&#60;/style&#62;</code></pre>\nを削除すれば戻ります。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:33;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:55:"맹조 :  &quot;サイトタイトルが消えた&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148632#post-206837";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 11:39:10 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206837@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:496:"<p>ウィジェットから『カテゴリー』と『アーカイヴ』のウィジェットを削除すればいいだけじゃないですか。</p>\n<blockquote><p>なぜならカテゴリーの順番が五十音順でバラバラだからです。</p></blockquote>\n<p>これはスラッグで並べ替えできます。<br />\n長年様々なユーザーが工夫し提案し、不便な部分は進歩してきたのです。そこまで WordPress はダメダメじゃないですよ。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:6:"맹조";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}i:34;a:6:{s:4:"data";s:30:"\n			\n			\n			\n			\n			\n			\n					";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";s:5:"child";a:2:{s:0:"";a:5:{s:5:"title";a:1:{i:0;a:5:{s:4:"data";s:57:"MINTBABY :  &quot;サイトタイトルが消えた&quot;";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:56:"https://ja.forums.wordpress.org/topic/148632#post-206836";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Tue, 07 Apr 2015 11:30:33 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:39:"206836@https://ja.forums.wordpress.org/";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:465:"<p>ごめんなさい、何度も投稿してくださって感謝してるんですけど、<br />\n私がしたいことは、<br />\n左の枠「濃いグリーン」categoriesとArchiveを消したいだけです。<br />\nなぜならカテゴリーの順番が五十音順でバラバラだからです。</p>\n<p>アーカイヴに表示される見出しの文字列、◯◯のアーカイヴ、◯◯のカテゴリーについてはわかりません。\n</p>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:8:"MINTBABY";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:36:"https://ja.forums.wordpress.org/rss/";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:7:{s:6:"server";s:5:"nginx";s:4:"date";s:29:"Wed, 08 Apr 2015 09:26:35 GMT";s:12:"content-type";s:23:"text/xml; charset=UTF-8";s:10:"connection";s:5:"close";s:4:"vary";s:15:"Accept-Encoding";s:13:"last-modified";s:29:"Wed, 08 Apr 2015 08:17:48 GMT";s:15:"x-frame-options";s:10:"SAMEORIGIN";}s:5:"build";s:14:"20130910190210";}', 'no');
