INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-03-31 02:58:25', '2015-03-31 02:58:25', 'WordPress へようこそ。これは最初の投稿です。編集もしくは削除してブログを始めてください !', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2015-03-31 02:58:25', '2015-03-31 02:58:25', '', 0, 'http://localhost.wpmulti/?p=1', 0, 'post', '', 1),
(2, 1, '2015-03-31 02:58:25', '2015-03-31 02:58:25', 'これはサンプルページです。同じ位置に固定され、(多くのテーマでは) サイトナビゲーションメニューに含まれるため、ブログ投稿とは異なります。サイト訪問者に対して自分のことを説明する自己紹介ページを作成するのが一般的です。たとえば以下のようなものになります。 \n\n<blockquote>はじめまして。昼間はバイク便のメッセンジャーとして働いていますが、俳優志望でもあります。これは僕のブログです。ロサンゼルスに住み、ジャックという名前のかわいい犬を飼っています。好きなものはピニャコラーダ (通り雨に濡れるのも) 。</blockquote>\n\nまたは、このようなものでもよいでしょう。\n\n<blockquote>XYZ 小道具株式会社は1971年の創立以来、高品質の小道具を皆様にご提供させていただいています。ゴッサム・シティに所在する当社では2,000名以上の社員が働いており、様々な形で地域のコミュニティへ貢献しています。</blockquote>\n\n新しく WordPress ユーザーになった方は、<a href="http://localhost.wpmulti/wp-admin/">ダッシュボード</a>へ行ってこのページを削除し、独自のコンテンツを含む新しいページを作成してください。それでは、お楽しみください !', 'サンプルページ', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2015-03-31 02:58:25', '2015-03-31 02:58:25', '', 0, 'http://localhost.wpmulti/?page_id=2', 0, 'page', '', 0),
(4, 1, '2015-04-03 03:13:16', '2015-04-03 03:13:16', '[theme-my-login]', 'Log In', '', 'publish', 'closed', 'closed', '', 'login', '', '', '2015-04-03 03:13:16', '2015-04-03 03:13:16', '', 0, 'http://localhost.wpmulti/login/', 0, 'page', '', 0),
(5, 1, '2015-04-03 03:13:16', '2015-04-03 03:13:16', '[theme-my-login]', 'Log Out', '', 'publish', 'closed', 'closed', '', 'logout', '', '', '2015-04-03 03:13:16', '2015-04-03 03:13:16', '', 0, 'http://localhost.wpmulti/logout/', 0, 'page', '', 0),
